    /**
     * An array of sample (dummy) items.
     */
    public static final List<SpeakerItem> ITEMS = new ArrayList<SpeakerItem>();

    private static final int COUNT = 25;

    static {
        // Add some sample items.
        for (int i = 1; i <= COUNT; i++) {
            addItem(createDummyItem());
        }
    }

    private static void addItem(SpeakerItem item) {
        ITEMS.add(item);
    }

    private static SpeakerItem createDummyItem() {
        Random random = new Random();
        final int randomNameIndex = random.nextInt(NAMES.length - 1);
        final String randomName = NAMES[randomNameIndex];
        final String randomDetails = DETAILS[random.nextInt(DETAILS.length - 1)];

        return new SpeakerItem(randomNameIndex, randomName, randomDetails);
    }

    /**
     * A dummy item representing a piece of title.
     */
    public static class SpeakerItem {
        public final String title;
        public final String details;
        public final String imageLink;

        public SpeakerItem(int imageIndex, String title, String details) {
            this.imageLink = String.format(Locale.ENGLISH, "https://randomuser.me/api/portraits/men/%d.jpg", imageIndex);
            this.title = title;
            this.details = details;
        }

        @Override
        public String toString() {
            return title;
        }
    }
